function musicaExiste(nomeMusica) {
  var index = 0;

  for (index; index < nomeMusicas.length; index++) {
    if (nomeMusicas[index] == nomeMusica) {
      return true;
    }
  }
  return false;
}

function slugfy(string) {
  return string
    .replace(/\s+/g, "-") // Replace spaces with -
    .trim()
    .toLowerCase();
}

function listaNaTela(nomeMusica, musica) {
  let localAdicionarMusica = document.getElementById("localAddMusica");
  let englobaMusica = document.createElement("div");
  let elemImgMusica = document.createElement("img");
  let elemnomeMusica = document.createElement("p");

  englobaMusica.setAttribute("id", slugfy(nomeMusica));
  elemImgMusica.setAttribute("src", musica);

  englobaMusica.append(elemImgMusica);
  englobaMusica.append(elemnomeMusica);
  localAdicionarMusica.append(englobaMusica);

  elemnomeMusica.innerHTML = nomeMusica;
}

imagemMusicas.forEach((element, index) => {
  listaNaTela(nomeMusicas[index], element);
});

function removeMusica() {
  let nomeMusica = document.getElementById("nomeMusica").value;
  let msgErro = document.getElementById("msgErro");

  msgErro.innerHTML = "Esse musica não existe!";

  for (let index = 0; index < nomeMusicas.length; index++) {
    if (nomeMusicas[index] == nomeMusica) {
      let divPai = document.getElementById("localAddMusica");
      let slugDiv = slugfy(nomeMusica);
      let divRemove = document.getElementById("" + slugDiv + "");
      console.log(slugDiv);
      nomeMusicas.splice(index, 1);
      imagemMusicas.splice(index, 1);

      divPai.removeChild(divRemove);
      msgErro.innerHTML = "Removido";
    }
  }
}

function adicionarMusica() {
  let valorLocalnomeMusica = document.getElementById("nomeMusica").value;
  let valorLocalmusica = document.getElementById("musica")
    .value;
  let localMsgErro = document.getElementById("msgErro");

  localMsgErro.innerHTML = "";
  if (valorLocalnomeMusica.trim() == "") {
    localMsgErro.innerHTML = "Insira o nome do musica por favor!";
  } else {
    if (!musicaExiste(valorLocalnomeMusica)) {
      if (valorLocalmusica.endsWith(".jpg")) {
        listaNaTela(valorLocalnomeMusica, valorLocalmusica);
        nomeMusicas.push(valorLocalnomeMusica);
        imagemMusicas.push(valorLocalmusica);
      } else {
        localMsgErro.innerHTML = "Insira o Endereço da imagem por favor!";
      }
    } else {
      localMsgErro.innerHTML = "Esse musica já existe na lista!";
    }
  }
}
